static void invoke_player_command(int NCode, int Id)
{
    printf("[%s] id=%d, NCode=%d, \n", __func__, Id, NCode);
    if (NCode == WM_NOTIFICATION_CLICKED)
    {
        switch (Id)
        {
        case ID_IMAGE_0://Play
            player_play();
            break;
        case ID_IMAGE_1://Stop
            player_pause();
            break;
        case ID_IMAGE_2://Back
            player_stop();
            break;
        }
    }
}

static void play_first_media(void)
{
    printf("[%s]\n", __func__);
    player_start();
    player_play();
}

void update_gui_widgets(WM_HWIN hWin, E_NM_PLAY_STATUS eStatu_now)
{
    static E_NM_PLAY_STATUS eStatus = eNM_PLAY_STATUS_ERROR;
    static uint64_t u64CurrentInSec_Current = 0;
    static uint64_t u64CurrentInSec_Total = 0;
    static uint64_t u64CurrentInPercent_Current = 0;

    uint64_t u64CurrentInSec_Current_now = player_playtime_current() / 1000;
    uint64_t u64CurrentInSec_Total_now = player_playtime_total() / 1000;
    uint64_t u64CurrentInPercent_Current_now = u64CurrentInSec_Current_now * 100 / u64CurrentInSec_Total_now;

    if (eStatus != eStatu_now)
    {
        if (hWin)
        {
            WM_HWIN  hItem_play = WM_GetDialogItem(hWin, ID_IMAGE_0);
            WM_HWIN  hItem_pause = WM_GetDialogItem(hWin, ID_IMAGE_1);
            switch (eStatu_now)
            {
            case eNM_PLAY_STATUS_PLAYING:
                WM_ShowWindow(hItem_pause);
                WM_HideWindow(hItem_play);
                break;

            case eNM_PLAY_STATUS_PAUSE:
                WM_ShowWindow(hItem_play);
                WM_HideWindow(hItem_pause);
                break;
            }
        }
        eStatus = eStatu_now;
    }

    // Total time string.
    if (u64CurrentInSec_Total != u64CurrentInSec_Total_now)
    {
        char szTimeStr[16];
        int HH = u64CurrentInSec_Total_now / 60 / 60 % 24;
        int MM = u64CurrentInSec_Total_now / 60 % 60;
        int SS = u64CurrentInSec_Total_now % 60;

        snprintf(szTimeStr, sizeof(szTimeStr), "%02d:%02d:%02d", HH, MM, SS);
        WM_HWIN hItem_total = WM_GetDialogItem(hWin, ID_TEXT_1);
        TEXT_SetText(hItem_total, szTimeStr);
        u64CurrentInSec_Total = u64CurrentInSec_Total_now;
    }

    // Current time string.
    if (u64CurrentInSec_Current != u64CurrentInSec_Current_now)
    {
        char szTimeStr[16];
        int HH = u64CurrentInSec_Current_now / 60 / 60 % 24;
        int MM = u64CurrentInSec_Current_now / 60 % 60;
        int SS = u64CurrentInSec_Current_now % 60;

        snprintf(szTimeStr, sizeof(szTimeStr), "%02d:%02d:%02d", HH, MM, SS);
        WM_HWIN hItem_current = WM_GetDialogItem(hWin, ID_TEXT_0);
        TEXT_SetText(hItem_current, szTimeStr);
        u64CurrentInSec_Current = u64CurrentInSec_Current_now;
    }

    // Progressbar
    if (u64CurrentInPercent_Current != u64CurrentInPercent_Current_now)
    {
        WM_HWIN      hItem_progressbar = WM_GetDialogItem(hWin, ID_SLIDER_0);
        SLIDER_SetRange(hItem_progressbar, 0, 100);
        SLIDER_SetValue(hItem_progressbar, u64CurrentInPercent_Current_now);
        u64CurrentInPercent_Current = u64CurrentInPercent_Current_now;
    }

}

